import React from "react";
import $ from "jquery";

const StateEffectEx = () => {
  const [department, setDepartment] = React.useState([]);

  React.useEffect(() => {
    $.get("http://itpaper.co.kr/demo/react/api/dept_list.php", {}, (json) => {
      setDepartment(json.item);
    });
  }, []);

  const onTrClick = (e) => {
    const current = $(e.currentTarget);
    current.css("background-color", "#ff0");
    $("tr").not(current).css("background-color", "#fff");
  };

  return (
    <div>
      <h2>StateEffectEx</h2>
      <table border="1">
        <thead>
          <tr>
            <th>학과번호</th>
            <th>학과명</th>
            <th>학과위치</th>
          </tr>
        </thead>
        <tbody>
          {department.map((item, index) => {
            return (
              <tr key={item.deptno} onClick={onTrClick}>
                <td>{item.deptno}</td>
                <td>{item.dname}</td>
                <td>{item.loc}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default StateEffectEx;
