import React from "react";

const MyEffect = () => {
  const [mySize, setMySize] = React.useState(320);

  return (
    <div>
      <h2>MyEffect</h2>

      <div>
        <input
          type="range"
          min="40"
          max="640"
          value={mySize}
          step="1"
          onChange={(e) => {
            setMySize(e.currentTarget.value);
          }}
        ></input>
      </div>
      <img
        alt="Hello, React"
        width={mySize}
        src="http://placehold.it/640x480?text='ILOVEMING'"
      ></img>
    </div>
  );
};

export default MyEffect;
