import React from "react";
import $ from "jquery";

const StateEffectEx = () => {
  const [department, setDepartment] = React.useState([]);

  React.useEffect(() => {
    $.ajax({
      url: "http://itpaper.co.kr/demo/react/api/dept_list.php",
      method: "get",
      success: (json) => {
        console.log(json);
        $("#list").empty();

        json.item.forEach((v, i) => {
          const li = $("<li>").html(
            "학과이름: " + v.dname + "/학과번호 : " + v.deptno + "/위치" + v.loc
          );
          $("#list").append(li);
        });
      },
    });
  }, []);

  return (
    <div>
      <h2>StateEffectEx</h2>
      <table border="1">
        <thead>
          <tr>
            <th>학과번호</th>
            <th>학과명</th>
            <th>학과위치</th>
          </tr>
        </thead>
        <tbody>
          {department.map((item, index) => {
            "<td>item.dname</td>";
          })}
        </tbody>
      </table>
    </div>
  );
};

export default StateEffectEx;
